import React, { useState } from 'react';

const insuranceCompanies = [
  {
    name: "HealthSecure Insurance",
    policies: "Health, Dental, Vision",
    coverage: "Covers hospitalization, outpatient services, preventive care, dental check-ups, and vision exams.",
    contact: "1-800-555-0199",
    website: "www.healthsecure.com",
    locations: "Available in all 50 states.",
    ratings: "4.7/5 based on customer reviews.",
    specialties: "Specializes in family health plans and preventive care."
  },
  {
    name: "LifeGuard Life Insurance",
    policies: "Term Life, Whole Life, Universal Life",
    coverage: "Provides financial security for beneficiaries, coverage for funeral expenses, and cash value accumulation for whole and universal policies.",
    contact: "1-800-555-0177",
    website: "www.lifeguard.com",
    locations: "Nationwide coverage with local agents.",
    ratings: "4.5/5 based on customer satisfaction surveys.",
    specialties: "Tailored life insurance plans for individuals and families."
  },
  {
    name: "AutoShield Insurance",
    policies: "Auto, Motorcycle, RV",
    coverage: "Covers vehicle repairs, liability, collision, and comprehensive coverage, including roadside assistance.",
    contact: "1-800-555-0222",
    website: "www.autoshield.com",
    locations: "Available in over 40 states.",
    ratings: "4.8/5 from customer feedback.",
    specialties: "Focuses on affordable plans for young drivers and high-risk individuals."
  },
  {
    name: "HomeGuard Insurance",
    policies: "Homeowners, Renters, Condo",
    coverage: "Covers property damage, personal liability, and loss of personal belongings.",
    contact: "1-800-555-0311",
    website: "www.homeguard.com",
    locations: "Available in all major cities across the country.",
    ratings: "4.6/5 based on claims experience.",
    specialties: "Offers customizable plans and discounts for smart home devices."
  },
  {
    name: "TravelSafe Insurance",
    policies: "Travel Health, Trip Cancellation, Baggage Loss",
    coverage: "Provides emergency medical coverage, trip interruption, cancellation insurance, and lost baggage assistance.",
    contact: "1-800-555-0432",
    website: "www.travelsafe.com",
    locations: "Coverage available worldwide.",
    ratings: "4.9/5 based on user experiences.",
    specialties: "Ideal for frequent travelers and adventure seekers."
  },
];

const InsuranceInfo = () => {
  const [location, setLocation] = useState('');
  const [expandedIndex, setExpandedIndex] = useState(null);

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  const toggleExpand = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'left', alignItems: 'center', height: '100vh', flexDirection: 'column' }}>
      <img src="/insurance.png" alt="Insurance" style={{ width: '1200px', height: 'auto', marginBottom: '20px' }} />

      <input
        type="text"
        placeholder="Enter your location"
        value={location}
        onChange={handleLocationChange}
        style={{ marginBottom: '20px', padding: '10px', width: '300px' }}
      />

      <h3>Insurance Companies in {location}</h3>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {insuranceCompanies.map((company, index) => (
          <li key={index} style={{ marginBottom: '15px' }}>
            <div
              style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}
              onClick={() => toggleExpand(index)}
            >
              <span style={{ marginRight: '10px' }}>
                {expandedIndex === index ? '▼' : '▶'} {/* Triangle toggle */}
              </span>
              <strong>{company.name}</strong>
            </div>
            <div 
              style={{
                maxHeight: expandedIndex === index ? '200px' : '0',
                overflow: 'hidden',
                transition: 'max-height 0.3s ease',
                marginTop: '10px',
                paddingLeft: '20px',
              }}
            >
              <p><strong>Policies:</strong> {company.policies}</p>
              <p><strong>What they cover:</strong> {company.coverage}</p>
              <p><strong>Contact:</strong> {company.contact}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default InsuranceInfo;
